INSERT INTO PHIM (MaPhim, TenPhim, MaTheLoai, ThoiLuong, NgayCongChieu, TrangThai, MoTa, DuongDan, MaNCC, HinhAnh) VALUES
('PH00001', N'Thám Tử Conan: Kẻ Hành Pháp Zero', 'TL001', 120, '2018-01-01', N'Ngừng chiếu', N'Conan Movie 22 xoay quanh nhân vật Tooru "Zero" Amuro và nghi vấn cảnh sát trưởng Kuroda là một trong những thành phần của tổ chức áo đen.', 'videos/1-201804928.mp4', 'NCC001', 'phim/1-201804928.jpg'),
('PH00002', N'Cớm Tốt Cớm Xấu 2', 'TL002', 130, '2018-01-01', N'Ngừng chiếu', N'8 năm trước, Martin Ward và David Bouchard dã gặp nhau một cách đầy ngoạn mục tại biên giới giữa Quebec và Ontario. Hai người đàn ông vẫn còn làm bạn với nhau nhưng thời gian dần trôi, họ đã không còn trò chuyện cả năm trời. Trong phần này, Ward và Bouchard phải đối mặt với một vụ trộm cắp xe hơi lớn mà hóa ra ẩn chứa trong đó còn rất nhiều uẩn khúc.', 'videos/1-201710810.mp4', 'NCC002', 'phim/1-201710810.jpg'),
('PH00003', N'Cô Gái Báo Thù', 'TL003', 100, '2018-01-01', N'Ngừng chiếu', N'Câu chuyện kể về Miki Takaraishi, một cô gái xinh đẹp 24 tuổi tốt nghiệp đại học Tokyo danh tiếng với thứ hạng đầu và từng đoạt danh hiệu Miss Campus, vì vậy Miki được xem là...', 'videos/1-201712312.mp4', 'NCC003', 'phim/1-201712312.jpg'),
('PH00004', N'Kẻ Hủy Diệt 4', 'TL004', 140, '2019-02-10', N'Đang chiếu', N'Một trận chiến khốc liệt giữa con người và các cỗ máy trong tương lai, nơi mà một đội quân người phải chiến đấu để bảo vệ sự sống cho loài người.', 'videos/1-20190501.mp4', 'NCC004', 'phim/1-20190501.jpg'),
('PH00005', N'Siêu Nhân Đặc Cảnh', 'TL005', 110, '2019-05-15', N'Đang chiếu', N'Siêu nhân đặc cảnh một lần nữa phải đối mặt với các tổ chức tội phạm quốc tế trong một nhiệm vụ đầy thử thách.', 'videos/1-20190615.mp4', 'NCC005', 'phim/1-20190615.jpg'),
('PH00006', N'Cuộc Chiến Thế Giới 3', 'TL006', 150, '2020-01-20', N'Ngừng chiếu', N'Một trận chiến hoành tráng giữa các quốc gia và những thế lực thù địch trong một tương lai không xa, khi các cường quốc tranh giành quyền lực tối cao.', 'videos/1-20200121.mp4', 'NCC006', 'phim/1-20200121.jpg'),
('PH00007', N'Siêu Anh Hùng Vô Song', 'TL007', 120, '2020-04-11', N'Đang chiếu', N'Siêu anh hùng trong phần mới này sẽ đối mặt với kẻ thù đáng sợ hơn, không chỉ là mối đe dọa cho thế giới mà còn là những bí mật lớn.', 'videos/1-20200411.mp4', 'NCC007', 'phim/1-20200411.jpg'),
('PH00008', N'Vượt Thời Gian', 'TL004', 125, '2020-06-18', N'Ngừng chiếu', N'Một bộ phim viễn tưởng nói về cuộc chiến tranh kéo dài hàng nghìn năm, nơi những người dám vượt thời gian để ngừng lại những cuộc chiến vô nghĩa.', 'videos/1-20200618.mp4', 'NCC008', 'phim/1-20200618.jpg'),
('PH00009', N'Tiền Lợi Hại', 'TL003', 115, '2021-01-10', N'Đang chiếu', N'Một bộ phim về thế giới ngầm của các tổ chức tội phạm quốc tế, nơi mà tiền là vũ khí mạnh nhất và ai nắm giữ tiền sẽ nắm giữ quyền lực.', 'videos/1-20210110.mp4', 'NCC009', 'phim/1-20210110.jpg'),
('PH00010', N'Muôn Màu Cuộc Sống', 'TL001', 100, '2021-03-15', N'Ngừng chiếu', N'Một bộ phim hài về những câu chuyện cuộc sống qua các giai đoạn, đầy màu sắc và cảm xúc, mang đến một thông điệp ý nghĩa.', 'videos/1-20210315.mp4', 'NCC010', 'phim/1-20210315.jpg'),
('PH00011', N'Chuyến Tàu Tốc Hành', 'TL001', 135, '2021-06-20', N'Đang chiếu', N'Một chuyến tàu tốc hành đầy bất ngờ, nơi hành khách phải đối mặt với những mối nguy hiểm lớn, quyết định sống còn trong giây phút cuối cùng.', 'videos/1-20210620.mp4', 'NCC010', 'phim/1-20210620.jpg'),
('PH00012', N'Mai Sau Đến', 'TL002', 110, '2021-09-05', N'Ngừng chiếu', N'Một câu chuyện tình yêu không có hồi kết, với những thách thức từ quá khứ và tương lai, bộ phim đi theo một mạch phim hành động tình cảm.', 'videos/1-20210905.mp4', 'NCC001', 'phim/1-20210905.jpg'),
('PH00013', N'Người Bảo Vệ Tự Do', 'TL003', 140, '2022-01-01', N'Đang chiếu', N'Cuộc chiến không khoan nhượng của những chiến binh vì tự do, không sợ chết, và đấu tranh đến cùng để bảo vệ những giá trị tối cao.', 'videos/1-20220101.mp4', 'NCC003', 'phim/1-20220101.jpg'),
('PH00014', N'Một Đời Lập Dị', 'TL004', 125, '2022-04-22', N'Ngừng chiếu', N'Một câu chuyện về một nhân vật kỳ lạ và hành trình tìm kiếm sự thực về cuộc đời mình, dù mọi người xung quanh đều nghĩ anh ta điên.', 'videos/1-20220422.mp4', 'NCC004', 'phim/1-20220422.jpg'),
('PH00015', N'Phim Kinh Dị: Màn Đêm Lạnh Lẽo', 'TL005', 105, '2022-07-11', N'Đang chiếu', N'Một bộ phim kinh dị tâm lý về một gia đình bị ám ảnh bởi những hiện tượng kỳ lạ trong ngôi nhà mới, khi bí mật cũ dần được hé lộ.', 'videos/1-20220711.mp4', 'NCC005', 'phim/1-20220711.jpg');
INSERT INTO PHIM (MaPhim, TenPhim, MaTheLoai, ThoiLuong, NgayCongChieu, TrangThai, MoTa, DuongDan, MaNCC, HinhAnh) VALUES
('PH00016', N'Bậc Thầy Phong Thủy', 'TL001', 126, '2018-03-27', N'Ngừng chiếu', N'Đánh dấu sự trở lại của tài tử Jo Seung Woo sau ba năm vắng bóng màn ảnh rộng kể từ hit 18+ Inside Men. Dự án này có kinh phí 12 tỉ won (khoảng 249 tỉ VND) và là phần phim cuối cùng trong một bộ ba phim lịch sử Hàn Quốc, hai tác phẩm trước là The Face Reader (2013) và The Princess and the Matchmaker (2018).', 'videos/1-201811345.mp4', 'NCC001', 'phim/1-201811345.jpg'),
('PH00017', N'Nạn Đói Năm 47', 'TL007', 100, '2018-03-17', N'Ngừng chiếu', N'Trong thời kỳ nạn đói xảy ra ở Ai-Len, một người Ai-Len tên là Ranger đã chiến đấu trong quân đội Anh ở nước ngoài, ông từ bỏ vị trí của mình để trở về đoàn tụ với gia đình. Mặc dù trải qua những kinh hoàng trong chiến tranh nhưng ông vẫn bị sốc khi chứng kiến nạn đói ở quê hương ảnh hưởng đến người dân cũng như với gia đình của mình.', 'videos/1-201811131.mp4', 'NCC002', 'phim/1-201811131.jpg'),
('PH00018', N'Cuồng Tín', 'TL005', 45, '2018-03-26', N'Ngừng chiếu', N'Diễn ra vào năm 1983, Red là một thợ gỗ sống trong một cabin hẻo lánh trong rừng. Bạn gái nghệ sĩ của anh, Mandy, dành cả ngày đọc những cuốn sách bìa mềm. Rồi một ngày nọ, cô lọt vào mắt của một kẻ lãnh đạo giáo phái điên cuồng, toàn những kẻ cưỡi xe máy. Hắn đã sai chúng đi bắt cóc cô nhưng không được thuận theo nên đã thiêu cô trước mặt Red. Bừng tỉnh trong cơn giận dữ và nỗi đau, Red trang bị vũ khí rồi lên đường săn tìm lũ cuồng tín.', 'videos/1-201809184.mp4', 'NCC003', 'phim/1-201809184.jpg'),
('PH00019', N'Những Cô Nàng Hắc Ám', 'TL003', 100, '2018-03-12', N'Ngừng chiếu', N'Một ngày nọ, Itsumi Shiraishi (Marie Iitoyo) chết tại trường THPT Nữ sinh Seibo Maria. Cô ngã từ sân thượng của trường. Itsumi là nữ sinh ưu tú và nổi tiếng nhất trường nên mọi người đều quan tâm đến cái chết kỳ lạ của cô ấy.', 'videos/1-201810694.mp4', 'NCC004', 'phim/1-201810694.jpg'),
('PH00020', N'Vũ Khí Bí Ẩn', 'TL006', 100, '2018-03-13', N'Ngừng chiếu', N'Là một bộ phim khoa học viễn tưởng (Sci-fi) kể về hành trình chạy trốn của một cậu bé da màu (14 tuổi) và người anh trai nuôi khỏi sự truy sát của một tên tội phạm khát máu và hai kẻ “giấu mặt”.', 'videos/1-201811267.mp4', 'NCC005', 'phim/1-201811267.jpg'),
('PH00021', N'Chuyện Kỳ Bí Ở Kamakura', 'TL002', 100, '2018-03-19', N'Ngừng chiếu', N'Dựa trên bộ manga có tuổi đời hơn 30 năm Kamakura Monogatari kể về Isshiki Nasakazu (Masato Sakai) - một tác giả viết truyện kỳ bí thích mặc trang phục truyền thống, anh sống cùng người vợ trẻ Akiko (Mitsuki Takahata). ở Kamakura, Nhật Bản. Isshiki Nasakazu có rất nhiều mối quan tâm khác như sưu tầm mô hình đường sắt, mô hình nhựa và cổ vật, nghiên cứu tội phạm và điều tra các hiện tượng diêu nhiên.', 'videos/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201811361.mp4', 'NCC006', 'phim/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201811361.jpg'),
('PH00022', N'Lời Nhắn Của Oan Hồn', 'TL005', 120, '2018-03-20', N'Ngừng chiếu', N'Mười năm sau thảm hoạ hạt nhân tại Chicago, thế giới tồn tại khe nứt giữa người sống và người chết. Ronnie (Bella Thorne) nhận được một thông điệp đe dọa từ một hồn ma xa lạ. Cô cùng một người bạn bí ẩn, Kirk, rơi vào thế giới bóng ma, và bắt đầu một cuộc đua tuyệt vọng với thời gian đối đầu một tên giết người xảo quyệt.', 'videos/1-201811727.mp4', 'NCC007', 'phim/1-201811727.jpg'),
('PH00023', N'Tình Yêu Tuổi Mới Lớn', 'TL006', 100, '2018-03-23', N'Ngừng chiếu', N'Vì muốn trả thù Kiều – cậu bạn cùng bàn vì đã làm mình bị phạt, Vương Khải biệt ra thư tình gửi cho cậu ta nhưng không ngờ Kiều tin thật. Vì không muốn làm bạn bị tổn thương, Vương Khải đã nhờ 1 bạn nữ mà cậu cũng có tình cảm làm bạn gái tạm thời với Kiều. Mọi việc trở nên rắc rối khi nhà trường ban lệnh cấm yêu đương.', 'videos/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201810303.mp4', 'NCC008', 'phim/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201810303.jpg'),
('PH00024', N'Săn Bão', 'TL001', 102, '2018-04-15', N'Ngừng chiếu', N'Trong một vụ án tưởng chừng như đi vào bế tắc của điều tra viên Lục Chí Liêm (Cổ Thiên Lạc) cùng đồng nghiệp, thì anh ta bị cáo buộc dính đến vụ việc quan liêu nhũng nhiễu ngay trong chính vụ án của mình. Để minh oan cho bản thân, Lục Chí Liêm đã dấn thân điều tra và phát hiện hàng loạt những đường dây bất hợp pháp. Từ đó, anh bắt đầu bước vào nhiều phen hiểm nguy cũng như những màn đấu trí đến nghẹt thở.', 'videos/1-201811746.mp4', 'NCC009', 'phim/1-201811746.jpg'),
('PH00025', N'Anh Yêu Em, Beth Cooper', 'TL002', 110, '2018-04-24', N'Ngừng chiếu', N'Một anh chàng mọt sách tuyên bố yêu cô gái nổi tiếng nhất trường – Beth Cooper – ngay trong bài diễn văn tốt nghiệp trung học. Hơn cả sự kinh ngạc, Beth tới trước cửa nhà anh chàng vào đêm đó và quyết định dành cho cậu bạn một đêm tuyệt nhất trong đời.', 'videos/2-201811186.mp4', 'NCC010', 'phim/2-201811186.jpg'),
('PH00026', N'Pripara: Cùng Nhau Đến Priparis', 'TL006', 73, '2018-04-29', N'Ngừng chiếu', N'Priparis được đặt tại Pháp và là trụ sở của Pripara toàn cầu dần mất đi ánh sáng. Nhận được thông điệp cầu cứu của Falulu, Laala và các thần tượng ở khắp nơi trên thế giới lập tức lên đường tới Priparis. Nếu ánh sáng không trở lại, Priparis sẽ biến mất buộc Laala và bạn bè cùng nhau nỗ lực hết sức giúp Falulu, cứu lấy thế giới.', 'videos/1-201811961.mp4', 'NCC001', 'phim/1-201811961.jpg'),
('PH00027', N'Sát Thủ Nhân Tạo', 'TL001', 125, '2018-04-13', N'Ngừng chiếu', N'Là bộ phim hành động li kì kể về Koo Ja-yoon – một cô bé được nuôi dưỡng trong một tổ chức đáng sợ - nơi diễn ra các cuộc thí nghiệm y học được thực hiện trên chính cơ thể con người nhằm biến họ thành những cỗ máy giết người. Sau khi chạy trốn khỏi tổ chức, Ja-yoon bị mất trí nhớ và được một cặp vợ chồng già nhận nuôi.', 'videos/1-201810546.mp4', 'NCC002', 'phim/1-201810546.jpg'),
('PH00028', N'Cô Gái Có Hình Xăm Rồng', 'TL002', 120, '2018-04-06', N'Ngừng chiếu', N'Là bộ phim chuyển thể từ cuốn sách đầu tiên trong bộ ba tiểu thuyết trinh thám Millennium của nhà văn Thụy Điển Stieg Larsson. Nhân vật chính Lisbeth Salander được ví như là một Sherlock Holmes phiên bản nữ bởi khả năng điều tra, phá án tài tình như một thám tử chuyên nghiệp. Lisbeth Salander là một cô gái 24 tuổi xăm trổ, bấm khuyên đầy mình nhưng lại sở hữu một trí tuệ phi phàm. Cô là một hacker thiên tài có thể đột nhập vào mạng lưới điều tra của cảnh sát để tìm thông tin về những bí ẩn chết người và lịch sử của một dòng họ giàu có trong thế giới tài chính.', 'videos/thegirlwiththedragontattooposter-201612606.mp4', 'NCC003', 'phim/thegirlwiththedragontattooposter-201612606.jpg'),
('PH00029', N'Quỷ Nhập Hồn', 'TL005', 105, '2018-04-20', N'Ngừng chiếu', N'Chuyện phim dựa trên câu chuyện có thật về cô bé Veronica (Sandra Escacena) đang ở tuổi 15. Là con gái lớn trong gia đình, cô bé phải vật lộn chăm sóc ba đứa em nhỏ sau cái chết của bố, giúp mẹ có thêm thời gian kiếm tiền nuôi sống gia đình.', 'videos/1-201811435.mp4', 'NCC004', 'phim/1-201811435.jpg'),
('PH00030', N'Ông Bác Siêu Nhân', 'TL006', 100, '2018-04-01', N'Ngừng chiếu', N'Ông Inuyashiki là một lão già khốn khổ có công việc và đời sống gia đình không mấy tốt đẹp. Bỗng một ngày, ông và một cậu học sinh tên Hiro chứng kiến một vụ nổ từ chiếc phi thuyền của người ngoài hành tinh và bị biến đổi thành một con người mới có sức mạnh siêu nhiên. Trong khi Hiro muốn hủy diệt thế giới thì ông Inuyashiki lại muốn dùng năng lực này để cứu người và ngăn cản Hiro.', 'videos/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201811500.mp4', 'NCC005', 'phim/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201811500.jpg'),
('PH00031', N'Quái Thú Vô Hình', 'TL001', 100, '2018-04-18', N'Ngừng chiếu', N'Những thợ săn bí ẩn từ không gian bỗng xuất hiện ở Trái Đất. Chính quyền cử một đội đặc nhiệm truy tìm và lùng bắt những thợ săn này nhưng liệu ai mới thật sự là kẻ đi săn. Bí ẩn về giống loài tiên tiến này dần được hé lộ.', 'videos/1-201810419.mp4', 'NCC006', 'phim/1-201810419.jpg'),
('PH00032', N'Charming: Hoàng Tử Đào Hoa', 'TL002', 100, '2018-05-06', N'Ngừng chiếu', N'Mang đến cho khán giả một câu chuyện mới, một góc nhìn cực kỳ thú vị, sáng tạo và chưa bao giờ được kể của ba cô gái xinh đẹp trong xứ sở thần tiên nổi tiếng: Bạch Tuyết, Lọ Lem và Công Chúa Ngủ Trong Rừng. Một ngày nọ cả ba khám phá ra rằng những vị hôn phu mà các cô đính hôn thật ra đều là cùng một người đó là Hoàng Tử Charming. Bên cạnh đó còn có hàng triệu cô gái trong vương quốc thầm thương trộm nhớ muốn trở thành vợ của Hoàng Tử.', 'videos/1-201810648.mp4', 'NCC007', 'phim/1-201810648.jpg'),
('PH00033', N'Chiến Tranh Lạnh', 'TL007', 88, '2018-05-08', N'Ngừng chiếu', N'Bộ phim hợp tác Ba Lan - Pháp - Anh này do đạo diễn Ba Lan sống ở Anh Pawel Pawlikowski đạo diễn hy vọng đoạt Cành cọ Vàng 2018. Cold War kể một câu chuyện tình tại châu Âu vào thập niên 1950 giữa hai người có xuất thân khác nhau. Đây cũng là phim mới nhất của Pawlikowski sau bộ phim Ida đoạt giải Bafta phim nước ngoài hay nhất năm 2015.', 'videos/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201811585.mp4', 'NCC008', 'phim/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201811585.jpg'),
('PH00034', N'Bạn Trai Cũ Tôi Là Điệp Viên', 'TL001', 90, '2018-05-02', N'Ngừng chiếu', N'Audrey và Morgan - đôi bạn thân tuổi băm bất ngờ rơi vào một âm mưu quốc tế khi phát hiện ra tay bạn trai đã “đá” Audrey là một điệp viên. Bị truy đuổi gay gắt bởi nhóm sát thủ theo dấu gã bạn trai, hai cô nàng bắt đầu hành trình vòng quanh Châu Âu và rơi vào hàng loạt tình huống cân não cười ra nước mắt để giải cứu thế giới.', 'videos/1-201810435.mp4', 'NCC009', 'phim/1-201810435.jpg'),
('PH00035', N'Sát Nhân Liên Hoàn', 'TL005', 120, '2018-05-23', N'Ngừng chiếu', N'Một kẻ giết người hàng loạt gây kinh hoàng cả thị trấn Martfü ở Hungary, nạn nhân chỉ toàn nữ giới...', 'videos/1-201811587.mp4', 'NCC010', 'phim/1-201811587.jpg'),
('PH00036', N'Thế Giới Động Vật', 'TL001', 135, '2018-05-29', N'Ngừng chiếu', N'Được cải biên từ tác phẩm truyện tranh Ultimate Survivor Kaiji (Gambling Apocalypse Kaiji) của tác giả Nobuyuki Fukumoto. Chàng trai chẳng làm được chuyện gì nên hồn - Trịnh Khai Tư (Lý Dịch Phong) - tham gia vào một trò chơi sinh tử đầy bí ẩn trên con tàu hạng sang nhằm trả nợ. Cách tính điểm của cuộc đấu này được dựa trên phương thức "oẳn tù tì". Vì người mẹ đang hôn mê và cô bạn thanh mai trúc mã Lưu Thanh (Châu Đông Vũ), Trịnh Khai Tư buộc phải sống sót trở về.', 'videos/1-201809686.mp4', 'NCC001', 'phim/1-201809686.jpg'),
('PH00037', N'Đứa Trẻ Bị Nguyền', 'TL005', 90, '2018-05-21', N'Ngừng chiếu', N'Lấy bối cảnh khi bóng đêm phủ lên một thị trấn nhỏ, nơi Samantha, một cô gái tuổi teen rắc rối đang chật vật tìm cho mình một gia đình nuôi mà cô thực sự thuộc về. Gia đình bố mẹ nuôi hiện tại của cô gồm Liz, Garrett và em gái Olivia.', 'videos/1-201809706.mp4', 'NCC002', 'phim/1-201809706.jpg'),
('PH00038', N'Người Mẹ Khác', 'TL003', 100, '2018-05-14', N'Ngừng chiếu', N'Phim trên tiểu thuyết "Dangshinui Bootak - My Other Mother" của Lee Dong-Eun. Hyo-Jin (Lim Soo-Jung) là một phụ nữ 32 tuổi. Hai năm trước, chồng cô qua đời trong một tai nạn để lại một cậu con trai 16 tuổi, Jong-Wook (Yoon Chan-Young), từ một cuộc hôn nhân trước đó, Hyo-Jin bắt đầu sống với Jong-Wook.', 'videos/1-201809358.mp4', 'NCC003', 'phim/1-201809358.jpg'),
('PH00039', N'Giữ Bóng Tối', 'TL006', 90, '2018-05-08', N'Ngừng chiếu', N'Sau cái chết của ba đứa trẻ bị nghi ngờ bị giết bởi những con sói, nhà văn Russell Core được thuê bởi cha mẹ của một cậu bé sáu tuổi mất tích để theo dõi và xác định vị trí con trai của họ trong vùng hoang dã Alaska.', 'videos/1-201809720.mp4', 'NCC004', 'phim/1-201809720.jpg'),
('PH00040', N'Bạch Vương', 'TL007', 100, '2018-06-22', N'Ngừng chiếu', N'Djata - một cậu bé 12 tuổi lớn lên ở một vùng đất nơi được cai trị dưới chế độ độc tài, và cậu bé không được tiếp cận với phần còn lại của thế giới. Từ nhỏ cậu đã được cha mình kể về nguồn gốc thật sự của nơi mình sinh ra, và bí mật về một kho báu được canh giữ bởi một người đàn ông tên là Pickaxe. Cho đến một ngày cha cậu bị hai người của chính phủ bắt đi...', 'videos/1-201809527.mp4', 'NCC005', 'phim/1-201809527.jpg'),
('PH00041', N'Chàng Trai Mứt Cam', 'TL002', 90, '2018-06-29', N'Ngừng chiếu', N'Miki là một nữ sinh trung học. Một ngày, gia đình Koishikawa tan vỡ khi ba mẹ Miki thông báo rằng họ sẽ li dị và tái hôn với người khác. Người họ tái hôn là cặp vợ chồng Matsuuras mà họ đã gặp ở Hawaii, và cặp vợ chồng đó cũng có một đứa con trai bằng tuổi Miki tên gọi là Yuu. Thế là 2 gia đình quyết định sẽ sống chung dưới một mái nhà.', 'videos/1-201809281.mp4', 'NCC006', 'phim/1-201809281.jpg'),
('PH00042', N'Chữa Lành Nỗi Đau', 'TL003', 110, '2018-06-01', N'Ngừng chiếu', N'Một người đàn ông mang trong mình một căn bệnh đã bất ngờ nhận được một món quà đặc biệt từ một cô gái trẻ.', 'videos/1-201809182.mp4', 'NCC007', 'phim/1-201809182.jpg'),
('PH00043', N'Quý Bà Đóa Hoa', 'TL005', 90, '2018-06-13', N'Ngừng chiếu', N'Một câu chuyện tuổi dậy thì xoay quanh một thiên tài lập dị, một cô gái bốc đồng và bạn cùng lớp của họ với những tính cách khác nhau. Huang Jian (Trương Nhược Quân) là một thiên tài trong lĩnh vực khoa học và công nghệ.', 'videos/1-201809258.mp4', 'NCC008', 'phim/1-201809258.jpg'),
('PH00044', N'Tòa Tháp Chọc Trời', 'TL001', 120, '2018-06-11', N'Ngừng chiếu', N'Dwayne Johnson sẽ vào vai cựu quân nhân và cựu trưởng nhóm đặc nhiệm giải cứu của FBI Will Ford đầy dũng cảm. Không may trong một nhiệm vụ nguy hiểm, tai nạn khủng khiếp xảy đến với Will làm anh mất đi chân trái của mình. Kể từ đó, Will Ford từ bỏ công việc tại FBI và trở thành chuyên gia đánh giá an ninh cho các tòa nhà.', 'videos/1-201804313.mp4', 'NCC009', 'phim/1-201804313.jpg'),
('PH00045', N'Lớp 8', 'TL002', 100, '2018-06-08', N'Ngừng chiếu', N'Một cô bé lớp 8 nhút nhát trong tuần cuối cùng của cấp hai để chuẩn bị lên học cấp ba.', 'videos/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201809442.mp4', 'NCC010', 'phim/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201809442.jpg'),
('PH00046', N'Trận Chiến Cuối Cùng', 'TL001', 90, '2018-06-15', N'Ngừng chiếu', N'Poong Ho muốn trả thù Jae Hee, người đã khiến anh trai mình rơi vào tình trạng hôn mê nhưng cậu biết mình không phải là đối thủ. Cậu bắt đầu học kĩ năng chiến đấu từ trưởng lão Hwang trước khi thách đấu Jae Hee.', 'videos/1-201809121.mp4', 'NCC001', 'phim/1-201809121.jpg'),
('PH00047', N'Công Chúa Ma Cà Rồng', 'TL005', 100, '2018-06-26', N'Ngừng chiếu', N'Dana là công chúa cuối cùng của dòng họ ma cà rồng đầy quyền lực. Chính vì thế, cô luôn bị Liên minh tội ác săn đuổi. Pasha là chàng trai si tình, tốt bụng bỗng phát hiện ra khả năng siêu nhiên khi cùng cô vào sinh ra tử.', 'videos/vampire-princessposter-27x39-01-709x1024-201703779.mp4', 'NCC002', 'phim/vampire-princessposter-27x39-01-709x1024-201703779.jpg'),
('PH00048', N'Điệp Viên Thiên Thần', 'TL006', 100, '2018-07-28', N'Ngừng chiếu', N'Câu chuyện thật về Ashraf Marwan, con rể của Tổng thống Nasser và cố vấn đặc biệt của người kế nhiệm Anwar Sadat - cũng là tài sản quý giá nhất của Cục Tình báo Israel trong thế kỷ 20. Dựa trên cuốn sách bán chạy nhất theo The New York Times "Thiên Thần: Điệp Viên Ai Cập Cứu Israel" của Uri Bar-Joseph.', 'videos/diep-201809942.mp4', 'NCC003', 'phim/diep-201809942.jpg'),
('PH00049', N'Di Truyền', 'TL003', 100, '2018-07-07', N'Ngừng chiếu', N'Có sự tham gia của Toni Collette, Gabriel Byrne, Alex Wolff và Milly Shapiro trong vai một gia đình bị quỷ ám. Nói chính xác hơn, họ bị ám bởi người bà trong dòng họ đã chết.', 'videos/1-201808605.mp4', 'NCC004', 'phim/1-201808605.jpg'),
('PH00050', N'Giai Điệu Trên Triền Dốc', 'TL002', 90, '2018-07-17', N'Ngừng chiếu', N'Lấy bối cảnh năm 1966, câu chuyện bắt đầu với nhân vật Kaoru Nishimi, khi cậu chuyển trường đến Kyushu (một trong 4 đảo chính ở Nhật Bản) để sống với họ hàng. Vốn là một người có sức khỏe yếu và bị chứng mất cân bằng tâm lý nên Kaoru không thể hòa nhập với môi trường mới một cách dễ dàng.', 'videos/1-201809514.mp4', 'NCC005', 'phim/1-201809514.jpg'),
('PH00051', N'Thử Thách Thần Chết 2: 49 Ngày Cuối Cùng', 'TL001', 90, '2018-07-24', N'Ngừng chiếu', N'Kể về chàng trai Su Hong đã được chọn là linh hồn thuần khiết thứ 49 cần giúp đỡ, cũng là linh hồn thuần khiết cuối cùng để cả 3 vệ thần hoàn thành sứ mệnh. Sau sứ mệnh này, họ cũng sẽ đầu thai làm kiếp người mới. Tuy nhiên trong cuộc hành trình 49 ngày cuối cùng này, thân phận của 3 vệ thần dần được hé lộ. Mọi thứ bắt đầu gay cấn hơn khi họ dần lấy lại những ký ức bi kịch của kiếp trước thông qua Gia Thần - một vị thần có quyền lực và vai trò không hề nhỏ ở dương thế.', 'videos/1-201809635.mp4', 'NCC006', 'phim/1-201809635.jpg'),
('PH00052', N'Cà Chua Bi', 'TL005', 120, '2018-07-21', N'Ngừng chiếu', N'Kể về Park Gu mưu sinh bằng nghề nhặt ve chai để cưng đứa cháu gái sống qua ngày. Tình cảnh của họ ngày càng tồi tệ hơn. Từ khi đứa con trai của ông ấy trở về và đã lấy đi số tiền mà ông ấy dành dụm mấy lâu và rồi chiếc xe ba gác của ông bị bọn côn đồ phá hỏng. Đó là phương tiện duy nhất giúp ông kiếm tiền. Ông ấy quyết định tới tìm người quản lý công trường. Nhưng không may, vì đứa cháu gái quá đói nên ông đành phải ăn cắp đồ ăn đứa bé. Không may đó là đồ ăn chó đã bị người giúp việc của quản lý công trường bỏ độc vì mối căm ghét của cô ta với ông chủ.', 'videos/m0010001cherrytomatoposterx230330-201809238.mp4', 'NCC007', 'phim/m0010001cherrytomatoposterx230330-201809238.jpg'),
('PH00053', N'Khách Sạn Huyền Bí 3: Kỳ Nghỉ Ma Cà Rồng', 'TL006', 100, '2018-07-28', N'Ngừng chiếu', N'Lần này sẽ là “cuộc chơi lớn” với một phen tiệc tùng sang chảnh hết nấc của gia đình Dracula. Đã quá “ngán” với cường độ làm việc chăm chỉ 365 ngày không nghỉ, bá tước Dracula quyết định đòi “đình công”. Để khai sáng cho người cha trăm tuổi chưa bao giờ bước ra khỏi “lũy tre làng”, vợ chồng nhà Jonathan – Mavis lập một kế hoạch xả hơi táo bạo: Thuê đứt một du thuyền du lịch hạng sang để đưa tất thảy bộ xậu quái vật già trẻ lớn bé làm một chuyến ra khơi nhớ đời.', 'videos/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201809687.mp4', 'NCC008', 'phim/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201809687.jpg'),
('PH00054', N'Thần Tốc', 'TL001', 120, '2018-07-28', N'Ngừng chiếu', N'Sau khi ở tù mười năm, người đàn ông biệt danh Driver được trả tự do, vừa ra tù thì anh ta liền lấy xe chạy đến một công ty rồi dùng súng bắn chết ông nhân viên trong công ty này. Trước đây, hai anh em Driver từng bị một băng cướp xông vào nhà sát hại, anh trai Driver là Gary bị cắt cổ, còn Driver bị tên trùm bắn.', 'videos/fastermovieposterbydgsway-d32yw08-201809953.mp4', 'NCC009', 'phim/fastermovieposterbydgsway-d32yw08-201809953.jpg'),
('PH00055', N'Săn Lùng Quái Thú', 'TL005', 100, '2018-07-27', N'Ngừng chiếu', N'Năm Trung Tông thứ 22 khi triều đại Joseon đang bị càn quét bởi trận dịch hạch kinh hoàng, đó là khi dân chúng đang chìm trong cảnh đói khổ lầm than với những nỗi kinh hoàng tưởng như không bao giờ chấm dứt. Lòng dân như lửa đốt khi tin đồn về một con quái thú khát máu chỉ chực chờ uống máu và ăn tươi nuốt sống những ai dám bén mảng đến ngọn núi Inwangsan.', 'videos/1-201811261.mp4', 'NCC001', 'phim/1-201811261.jpg'),
('PH00056', N'Chỉ Có Thể Hình Dung', 'TL003', 100, '2018-08-29', N'Ngừng chiếu', N'Dựa trên một câu chuyện có thật đầy cảm hứng đằng sau ca khúc đình đám của nhóm MercyMe, một bài hát mang nhiều ý nghĩa, niềm hi vọng cuộc sống, và là sức mạnh để khiến ta có thể mở lòng bao dung tha thứ.', 'videos/chi-201809227.mp4', 'NCC002', 'phim/chi-201809227.jpg'),
('PH00057', N'Người Chứng Kiến', 'TL006', 100, '2018-08-10', N'Ngừng chiếu', N'Xoay quanh một vụ án mạng xảy ra tại một căn hộ phức hợp. Mọi chuyện dần trở nên phức tạp khi người đàn ông tên Sang Hoon (Lee Sung Min) tình cờ nhìn vào mắt kẻ sát nhân.', 'videos/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201811722.mp4', 'NCC003', 'phim/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201811722.jpg'),
('PH00058', N'Tháp Lửa', 'TL001', 120, '2018-08-21', N'Ngừng chiếu', N'Đây là bộ phim hành động về thảm họa kinh hoàng nhất Hàn Quốc khi tòa tháp đôi 108 tầng Sky Tower ở Seoul bị chôn vùi trong biển lửa vào đúng đêm Giáng sinh khiến hàng nghìn người dân sống trong tòa nhà bị mắc kẹt trong sự hoảng loạn và nỗi sợ hãi tột độ.', 'videos/1-201811188.mp4', 'NCC004', 'phim/1-201811188.jpg'),
('PH00059', N'Venom', 'TL002', 120, '2018-08-27', N'Ngừng chiếu', N'Đã hé lộ thân phận nhân vật phản diện cực kỳ nguy hiểm và kinh hãi khi tung trailer chính thức khắp thế giới làm điên đảo fan hâm mộ trong thế giới của Marvel. Chàng phóng viên Eddie Brock bí mật theo dõi âm mưu xấu xa của một tổ chức và bị nhiễm phải Symbiote và trở thành quái vật Venom đầy nguy hiểm.', 'videos/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201811263.mp4', 'NCC005', 'phim/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201811263.jpg'),
('PH00060', N'Lời Thì Thầm', 'TL005', 120, '2018-08-22', N'Ngừng chiếu', N'Sáu học sinh trung học đã hoàn thành kỳ thi tuyển sinh đại học của họ, vô tình khám phá một ngôi nhà ma ám trong một công viên giải trí với những tin đồn kỳ lạ về nó. Trong sự tò mò, các thanh thiếu niên bỏ qua các cảnh báo và đi vào. Bên trong đó, các học sinh bắt đầu nghe thấy những tiếng thì thầm của cái chết và biến mất từng người một, một nỗi kinh hoàng khủng khiếp không thể ngăn cản được.', 'videos/1-201811273.mp4', 'NCC006', 'phim/1-201811273.jpg'),
('PH00061', N'Cổ Kiếm Kì Đàn: Lưu Nguyệt Chiêu Minh', 'TL006', 120, '2018-08-10', N'Ngừng chiếu', N'Dù trùng tên nhưng Cổ Kiếm Kỳ Đàm bản điện ảnh và truyền hình không liên quan gì đến nhau. Bộ phim điện ảnh Cổ Kiếm Kỳ Đàm: Chi Lưu Nguyệt Chiêu Minh xoay quanh hành trình tìm thanh kiếm Chiêu Minh đầy gian nguy của Nhạc Vô Dị, Văn Nhân Vũ, Hạ Di Tắc và A Nguyễn để ngăn cản tai nạn sắp xảy ra ở thành Lưu Nguyệt.', 'videos/1-201811864.mp4', 'NCC007', 'phim/1-201811864.jpg'),
('PH00062', N'Thiên Thần Công Lý', 'TL003', 100, '2018-08-19', N'Ngừng chiếu', N'Kể về Riley North (Jennifer Garner), một người vợ và cũng là một người mẹ có cuộc sống bị đảo lộn hoàn toàn sau vụ tấn công nhằm vào gia đình cô. Sau khi tỉnh dậy từ trong hôn mê và nhận ra hệ thống pháp luật và công lý đã trở nên vô năng trong việc trừng phạt những kẻ phạm tội, cô biến mất để rồi trở lại sau vài năm và lên kế hoạch trả thù.', 'videos/1-201811624.mp4', 'NCC008', 'phim/1-201811624.jpg'),
('PH00063', N'Đại Chiến Thành AnSi', 'TL001', 120, '2018-08-21', N'Ngừng chiếu', N'Cuộc chiến kéo dài 88 ngày đêm giữa quân đội Goguryeo và quân xâm lược nhà Đường để bảo vệ thành chiến Ansi. Một cuộc chiến không cân sức tưởng chừng như đã có kết quả an bài, nhưng kết cục lại không ai ngờ tới và được ghi danh vào sử sách như một trận đánh tiêu biểu cho tinh thần chiến đấu mãnh liệt.', 'videos/1-201811268.mp4', 'NCC009', 'phim/1-201811268.jpg'),
('PH00064', N'Vợ Ma', 'TL007', 120, '2018-09-04', N'Ngừng chiếu', N'Tại một thị trấn nhỏ ở Ấn Độ, những người đàn ông luôn sống trong sợ hãi bởi một hồn ma được gọi là "Stree" (Vợ) thường bắt cóc đàn ông vào ban đêm. Dựa trên truyền thuyết đô thị của "Nale Ba" đã lan truyền ở Karnataka vào những năm 1990.', 'videos/1-201811754.mp4', 'NCC010', 'phim/1-201811754.jpg'),
('PH00065', N'Trò Gắn Thẻ', 'TL002', 90, '2018-09-12', N'Ngừng chiếu', N'Một nhóm nhỏ các cậu bạn cùng lớp tổ chức một trò chơi phức tạp, thường niên suốt từ năm chín tuổi đến khi trưởng thành, yêu cầu họ phải đi khắp đất nước.', 'videos/1-201808143.mp4', 'NCC001', 'phim/1-201808143.jpg'),
('PH00066', N'Người Tù Khổ Sai', 'TL001', 130, '2018-09-14', N'Đang chiếu', N'Là câu chuyện sử thi của Henri "Papillon" Charrière (Charlie Hunnam), một kẻ chuyên đi phá két sắt từ thế giới ngầm Paris, người bị gài tội giết người và bị kết án tù chung thân tại nhà tù thuộc địa Đảo Quỷ. Quyết tâm giành lại quyền tự do của mình, Papillon đã “kết thân” với chuyên gia lừa đảo Louis Dega (Rami Malek) và cả hai đã lên một kế hoạch vượt ngục vĩ đại nhất trong lịch sử. Bộ phim được làm lại theo cốt truyện phim Papillon năm 1973 dựa cuốn hồi ký cùng tên bán chạy nhất nước Mỹ.', 'videos/1-201811546.mp4', 'NCC001', 'phim/1-201811546.jpg'),
('PH00067', N'A-X-L Chú Chó Robot', 'TL005', 100, '2018-09-27', N'Đang chiếu', N'Tiếp tục giúp khán giả có dịp tái ngộ tình bạn thiêng liêng đó trên màn ảnh rộng với A-X-L (tựa Việt hóa: A-X-L Chú Chó Robot) khi người bạn bốn chân của chúng ta nay đã trở thành một chú siêu khuyển bằng máy móc.', 'videos/1-201810393.mp4', 'NCC002', 'phim/1-201810393.jpg'),
('PH00068', N'Xác Chết Trở Về', 'TL003', 90, '2018-09-12', N'Đang chiếu', N'YOON Seol-hee, Chủ tịch của một tập đoàn lớn, được phát hiện đã tử vong trước đó. Xác của cô ta đặt trong nhà xác, đang chờ được khám nghiệm bởi Cục Pháp Y Quốc Gia, thì đột nhiên nó biến mất một cách bí ẩn không dấu vết. Trong khi người chồng cô ta đang bị nghi ngờ ra là thủ phạm, cảnh sát Joongsik quyết điều tra cho ra người đã đánh cắp cái xác và vì lí do gì...', 'videos/1-201804259.mp4', 'NCC003', 'phim/1-201804259.jpg'),
('PH00069', N'Bào Thai Quỷ', 'TL005', 100, '2018-09-22', N'Đang chiếu', N'Là bộ phim kinh dị khởi đầu cho mùa Halloween năm nay với nội dung phim xoay quanh một giáo sư đại học & người vợ mang thai đang chờ đứa con đầu lòng. Thế nhưng, sau một lần chạy bộ giữa rừng, người vợ liên tục bị tấn công bởi những hiện tượng kỳ lạ khiến cô bị sảy thai. Dường như cô đã vô tình giải thoát một thực thể ma quái khát máu ẩn náu trong rừng sâu.', 'videos/1-201810659.mp4', 'NCC004', 'phim/1-201810659.jpg'),
('PH00070', N'Cuộc Chiến Kim Cương Xanh', 'TL006', 90, '2018-09-18', N'Đang chiếu', N'Keanu Reeves vào vai Lucas Hill một thương gia đến Nga để bán những viên kim cương quý hiếm. Tuy nhiên cuộc giao dịch không thành công và anh trở thành đối tượng bị săn đuổi của những băng nhóm tội phạm và nhiều lực lượng chức năng khác.', 'videos/1-201807113.mp4', 'NCC005', 'phim/1-201807113.jpg'),
('PH00071', N'Pháp Sư Côn Đồ', 'TL001', 127, '2018-09-27', N'Đang chiếu', N'Kể về: Gwang Ho (Park Shin Yang) là cánh tay phải đáng tin cậy của một ông trùm xã hội đen. Trong một lần dùng tay đỡ lưỡi dao cho thủ lĩnh của mình, đường định mệnh trong lòng bàn tay của Gwang Ho đã thay đổi. Sau đó, một loạt các sự kiện kỳ lạ xảy đến khiến Gwang Ho quyết định tìm gặp các thầy bói.', 'videos/manontheedge-p1-201605302.mp4', 'NCC006', 'phim/manontheedge-p1-201605302.jpg'),
('PH00072', N'Người Dơi Ninja', 'TL002', 85, '2018-10-06', N'Đang chiếu', N'Batman cùng những đồng minh và kẻ thù bị đưa từ thành phố Gotham hiện đại trở về thời phong kiến Nhật Bản.', 'videos/vung-tu-dia-phan-3-into-the-badlands-season-3-2018-1-201804353.mp4', 'NCC007', 'phim/vung-tu-dia-phan-3-into-the-badlands-season-3-2018-1-201804353.jpg'),
('PH00073', N'Hoa Của Quỷ', 'TL003', 100, '2018-10-03', N'Đang chiếu', N'Nằm trong series phim Gogol đình đám và đầy tham vọng của điện ảnh Nga, HOA CỦA QUỶ (tựa gốc: GOGOL: VIY) lấy bối cảnh tại một ngôi làng bí ẩn Dikanka trong quá khứ xa xôi, nơi mà một thế lực đen tối đã bắt và giết chết các cô gái trẻ. Nikolai Gogol (Alexander Petrov thủ vai) – một pháp sư từ St Petersburg đã nhập cuộc điều tra. Anh dần phát hiện ra hung thủ đứng sau tất cả là Kỵ Sỹ Hắc Ám. Nhưng cuộc chiến chống lại thế lực đen tối không hề suôn sẻ, lúc này vị pháp sư trẻ tuổi cần đến sự giúp đỡ của người bạn đồng hành tuyệt vời là là Thợ Săn Phù Thủy kiêm Nhà Thần Học Khoma Brutu.', 'videos/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201810350.mp4', 'NCC008', 'phim/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201810350.jpg'),
('PH00074', N'Biệt Đội Cún Cưng', 'TL005', 100, '2018-10-11', N'Đang chiếu', N'Là câu chuyện xoay quanh hành trình phá án của Max và người bạn đặc biệt – một chú chó có khả năng suy luận vô cùng tài tình.', 'videos/biet-201811895.mp4', 'NCC009', 'phim/biet-201811895.jpg'),
('PH00075', N'Không Kích', 'TL001', 120, '2018-10-02', N'Đang chiếu', N'Lấy bối cảnh năm 1943 tại Trung Quốc thời Thế Chiến 2, Air Strike kể về cuộc đánh bom của Nhật Bản vào Trùng Khánh. 5 người Trung Quốc khác nhau chiến đấu theo cách của riêng họ xuyên suốt các cuộc tấn công của Không quân Nhật Bản để bảo vệ một cỗ máy quân sự quan trọng ở Trùng Khánh. Phim quy tụ dàn diễn viên toàn sao với Bruce Willis, Adrien Brody, Song Seung Hun, Lưu Diệp, Trần Vỹ Đình, Tạ Đình Phong và Phạm Băng Băng.', 'videos/1-201811559.mp4', 'NCC010', 'phim/1-201811559.jpg'),
('PH00076', N'Những Cô Gái Equestria: Tình Bạn Bị Lãng Quên', 'TL006', 45, '2018-10-30', N'Đang chiếu', N'Khi Sunset Shimmer phát hiện ra bạn bè của cô không nhớ gì về mình, cô bị sốc khi biết rằng sau khi Hunman Twilight được cải cách, tất cả dấu vết của SunSet đã biến mất. Tuyệt vọng, Sunset quyết tâm mang lại ký ức của bạn bè về mình. Nhưng để làm như vậy, trước tiên cô phải tìm ra thủ phạm đã làm việc này.', 'videos/1-201811551.mp4', 'NCC001', 'phim/1-201811551.jpg'),
('PH00077', N'Huynh Đệ Hoàng Kim', 'TL001', 120, '2018-10-26', N'Đang chiếu', N'Golden Job đánh dấu sự tái ngộ của dàn diễn viên đình đám Hong Kong trong một bộ phim sau 20 năm. Lần đầu tiên lên sóng vào năm 1996, bộ phim Người trong giang hồ (Young and Dangerous) đã nhận được sự quan tâm của dư luận. Năm anh em Sư Vương (Trịnh Y Kiện), Hỏa Sơn (Trần Tiểu Xuân), Bill (Tạ Thiên Hoa), Đàm Định (Tiền Gia Lạc), Lão Thử (Lâm Hiểu Phong), dưới sự chỉ đạo của ân sư Tào Sir (Tăng Chí Vĩ) cùng nhau vào sinh ra tử.', 'videos/1-201811732.mp4', 'NCC002', 'phim/1-201811732.jpg'),
('PH00078', N'Đường Đua Tử Thần 4', 'TL005', 100, '2018-10-16', N'Đang chiếu', N'Danny Trejo trở lại với tư cách là một Bookie tàn nhẫn, Goldberg, trong Death Race điên cuồng nhất, máu nhất. Sau một cuộc tấn công thất bại đối với tù nhân và người lái xe huyền thoại, Frankenstein, chuyên gia Black Ops, Connor Gibson (McGowan) xâm nhập vào một nhà tù liên bang tối đa với một mục tiêu – vào Death Race bất hợp pháp và bất hợp pháp và đưa Frankenstein xuống. Connor sử dụng sự trợ giúp của Baltimore Bob (Glover) và Lists (Koehler), và bất ngờ rơi vào tình yêu với vẻ đẹp bartending, Jane (Marzano).', 'videos/duong-dua-tu-than-4-poster-201811151.mp4', 'NCC003', 'phim/duong-dua-tu-than-4-poster-201811151.jpg'),
('PH00079', N'Ẩn Thân', 'TL003', 100, '2018-10-22', N'Đang chiếu', N'Kể về hai cha con sống ẩn dật trong rừng, tránh xa phần còn lại của thế giới. Một sai lầm khiến họ bị chính quyền phát hiện, buộc phải chạy trốn và tìm nơi ở mới.', 'videos/1-201811242.mp4', 'NCC004', 'phim/1-201811242.jpg'),
('PH00080', N'Nhiệm Vụ Bất Khả Thi: Sụp Đổ', 'TL001', 120, '2018-11-02', N'Đang chiếu', N'Ba năm sau Mission: Impossible – Rogue Nation, chàng đặc vụ điển trai và hào hoa bậc nhất trên màn ảnh rộng Ethan Hunt sẽ tái xuất màn bạc trong mùa hè 2018. Trailer đầu tiên của Nhiệm Vụ Bất Khả Thi: Sụp Đổ (Tựa gốc: Mission: Impossible - Fallout) vừa được hãng phát hành Paramount tung ra ngay lập tức trở thành tâm điểm của đại chúng bởi sự trở lại của ngôi sao hành động hàng đầu thế giới Tom Cruise cùng những khoảnh khắc nghẹt thở bắt nguồn từ pha hành động đẳng cấp mang đậm dấu ấn cá nhân anh.', 'videos/1-201809546.mp4', 'NCC005', 'phim/1-201809546.jpg'),
('PH00081', N'Con Nhà Siêu Giàu Châu Á', 'TL002', 100, '2018-11-03', N'Đang chiếu', N'Crazy Rich Asians theo chân cô gái người Mỹ gốc Á Rachel Chu cùng bạn trai Nick Young về quê nhà Singapore để dự đám cưới người bạn thân nhất. Khi đến nơi, Rachel sửng sốt khi phát hiện ra bạn trai là con của một trong những gia đình giàu có nhất châu Á, và là một trong những anh chàng độc thân hot nhất Singapore.', 'videos/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201811793.mp4', 'NCC006', 'phim/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201811793.jpg'),
('PH00082', N'Trận Bóng Kinh Hoàng', 'TL001', 100, '2018-11-16', N'Đang chiếu', N'Cho rằng gã anh trai phản bội Dimitri (Pierce Brosnan) đang lẩn trốn đâu đó tại sân vận động Boleyn Ground nơi diễn ra trận bán kết tranh cúp Châu Âu giữa hai đội tuyển West Ham United và Dynamo FCC, Arkady (Ray Stevenson), tên cầm đầu cuộc cách mạng tại bang Sukovia ở nước Nga đã phong tỏa toàn bộ sân vận động khiến nội bất xuất, ngoại bất nhập để truy tìm kẻ thù. Trong đám đông có mặt tại đó, Mike Knox (Dave Bautista) và đứa cháu của mình Danni (Lara Peake) đã vô tình có mặt tại trận cầu sống còn và phải đối mặt với băng nhóm nguy hiểm của tên Arkady. Liệu Mike có kịp thời ngăn chăn Arkady trước khi hắn tìm thấy Dimitri? Sự sống của 35,000 người có mặt tại sân vận động và người dân nước Nga phụ thuộc cả vào anh.', 'videos/1-201809342.mp4', 'NCC007', 'phim/1-201809342.jpg'),
('PH00083', N'Christopher Robin 2018', 'TL003', 104, '2018-11-29', N'Đang chiếu', N'Là một tác phẩm live-action ấm áp về tình cảm gia đình của hãng phim Disney. Bộ phim xoay quanh chuyến phiêu lưu trở lại tuổi thơ cùng những người bạn thuở nhỏ đầy thân thuộc bao gồm Pooh, Tigger, Eeyore và Piglet ở Rừng Trăm Mẫu. Năm tháng trôi qua, Robin dần trưởng thành và quên đi những người bạn thời thơ ấu. Anh phải đối mặt với những khó khăn trong công việc và rời xa chính gia đình mình. Buổi hội ngộ tình cờ của họ tại London đã khơi dậy niềm vui cho hành trình mới đầy cảm xúc và giúp anh tìm lại ý nghĩa trong cuộc sống.', 'videos/1-201811400.mp4', 'NCC008', 'phim/1-201811400.jpg'),
('PH00084', N'Ông Trùm Phía Sau', 'TL003', 120, '2018-11-12', N'Đang chiếu', N'Một ngày nọ, Zhong tỉnh dậy một mình trong biệt thự của mình: anh bị nhốt trong phòng ngủ của mình, và cửa sổ đã được xếp lên. Một người gọi bí ẩn thông báo cho anh ta rằng anh ta phải chơi một trò chơi: anh ta sẽ được đưa ra một loạt các lựa chọn không thể giữa một lựa chọn đau khổ A (ví dụ, công khai tiết lộ anh ta đang trốn thuế) và một lựa chọn không đau đớn B (chẳng hạn như hy sinh một bạn bè) - không chọn sẽ dẫn đến cả hai tùy chọn được thi hành. Trong khi cố gắng trốn thoát và khám phá danh tính của kẻ hành hạ.', 'videos/5b7c2c9469823-201811255.mp4', 'NCC001', 'phim/5b7c2c9469823-201811255.jpg'),
('PH00085', N'Người Lính Vô Danh', 'TL001', 100, '2018-11-02', N'Đang chiếu', N'Tháng 6 năm 1941: Bị mắc kẹt giữa hai chế độ đàn áp, Phần Lan có ít sự lựa chọn nhưng phải đồng minh với Đức Quốc xã chống lại Liên Xô, kẻ thù không đội trời chung...', 'videos/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201811687.mp4', 'NCC002', 'phim/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201811687.jpg'),
('PH00086', N'Chuyến Đi Của Cuộc Đời', 'TL002', 100, '2018-11-15', N'Đang chiếu', N'Mặc dù đã 83 tuổi nhưng bà Edie tin rằng không gì là quá muộn. Bà bỏ lại mọi việc phía sau và vác ba lô lên bắt đầu chuyến phiêu lưu chinh phục ngọn núi Suliven hùng vĩ ở Scotland.', 'videos/1-201811955.mp4', 'NCC003', 'phim/1-201811955.jpg'),
('PH00087', N'Màn Sương Chết', 'TL005', 102, '2018-11-18', N'Đang chiếu', N'Không được báo trước, một trận động đất vô cùng lớn tấn công Paris, cả thành phố sau đó bị bao phủ bởi một lớp sương mù dày đặc. Màn sương chết ấy đang lần lượt cướp đi tính mạng của rất nhiều những người dân nơi đây. Gia đình cô bé 11 tuổi Sarah - người được nuôi dưỡng trong lồng kính y tế đặc biệt từ khi vừa chào đời, may mắn là một trong số ít những người còn sống sót, nhưng họ cũng chỉ cách cái kết tàn nhẫn kia một làn hơi thở vô cùng mong manh.', 'videos/1-201811677.mp4', 'NCC004', 'phim/1-201811677.jpg'),
('PH00088', N'Mạng Lưới Ngầm', 'TL006', 100, '2018-12-25', N'Sắp chiếu', N'Một cậu học sinh bình thường, thậm chí vụng về, lại là đầu mối của mạng lưới buôn bán ma túy Columbia. “Mạng lưới ngầm” là một bộ phim đáng xem, có chiều sâu và để lại dấu ấn trong lòng khán giả.', 'videos/1-201811738.mp4', 'NCC005', 'phim/1-201811738.jpg'),
('PH00089', N'Nô Lệ Của Quỷ', 'TL003', 100, '2018-12-04', N'Đang chiếu', N'Nói về những điều kỳ lạ xảy ra trong một gia đình ba thế hệ sống ở vùng ngoại ô Jakarta. Người mẹ Mawarni là một nghệ sĩ nổi tiếng và có những ca khúc ăn khách. Một ngày nọ, Mawarni bị mắc một cơn bệnh lạ kéo dài hơn ba năm rồi qua đời. Và từ đây tấn bi kịch bắt đầu với những người ở lại. Điều đầu tiên là mẹ cô được phát hiện chết một cách bí ẩn trong giếng nước, tiếp theo đó là sự xuất hiện của những hồn ma lởn vởn trong nhà trêu ghẹo hăm dọa những đứa con, và đỉnh điểm là cái chết của người họ hàng đã giúp đỡ gia đình cô nhiệt tình lúc tang gia. Cô con gái lớn Rini sau khi đọc được di thư của bà ngoại thì biết được một sự thật kinh khủng là mẹ cô - bà Mawarni không thể có con, và bốn chị em cô được sinh ra là do mẹ cô tham gia một giáo phái Satanic bí ẩn để có thai.', 'videos/1-201811867.mp4', 'NCC006', 'phim/1-201811867.jpg'),
('PH00090', N'Ông Trăm Tuổi Chèo Qua Cửa Sổ Và Biến Mất', 'TL007', 120, '2018-12-08', N'Sắp chiếu', N'Vào ngày sinh nhật lần thứ 100 của mình, cụ ông Allan Karlsson đột nhiên trèo qua của sổ ngôi nhà dưỡng lão - Nhà Già - và biến mất. Ở cái tuổi 100 hiếm ai đạt tới thì cụ có thể đi đâu được? Một cuộc truy tìm trên khắp nước Thụy Điển diễn ra từ phía những người có trách nhiệm chăm nom cụ cũng như chính quyền sở tại. Song song với cuộc truy tìm nhân đạo ấy, một cuộc truy tìm đuổi bắt khác gay cấn hơn, xảy đến từ một tên tội phạm, kẻ đã ngớ ngẩn hoặc đúng hơn, bất cẩn trao vali 50 triệu crown vào tay cụ già này. Nhưng một người đã sống qua một thế kỷ thì không dễ gì tóm cụ ta được.', 'videos/1-201811452.mp4', 'NCC007', 'phim/1-201811452.jpg'),
('PH00091', N'Binh Pháp II: Sự Phản Bội', 'TL001', 120, '2018-12-21', N'Sắp chiếu', N'Khi điệp vụ Neil Shaw “lộ diện” để minh oan cho người được cho là đã giết chết người cố vấn cũ của anh, Neil đã phải đối mặt với một loạt các vụ trả thù. Dưới sự thuyết phục của một người bạn và một ứng cử viên cho chiếc ghế trong thượng viện, Neil Shaw một lần nữa phải dấn thân để lập lại trật tự.', 'videos/1-201811780.mp4', 'NCC008', 'phim/1-201811780.jpg'),
('PH00092', N'Gia Đình Siêu Nhân 2', 'TL006', 102, '2018-12-25', N'Sắp chiếu', N'Đánh dấu những chuyến phiêu lưu anh hùng đầy hấp dẫn với sự đổi vai đầy táo bạo. Lần này, mẹ dẻo Helen (Elastigirl) sẽ đi thực chiến giải cứu thế giới trong khi bố khỏe Bob (Mr. Incredible) lùi về hậu phương trông nom những đứa con siêu nhân láu lỉnh gồm: con gái Violet (siêu năng lực tàng hình và tạo ra từ trường làm chắn bảo vệ), con trai Dash (chạy siêu nhanh) và cậu út Jack-Jack với sức mạnh chưa được khám phá.', 'videos/1-201810404.mp4', 'NCC009', 'phim/1-201810404.jpg'),
('PH00093', N'Thiện Ác Đối Đầu 2', 'TL001', 120, '2018-12-28', N'Sắp chiếu', N'Là một bộ phim hành động cực kỳ gây cấn và kịch tính với sự tham gia diễn xuất trở lại của diễn viên gạo cội Denzel Washington. Trong phim, cựu đặc vụ CIA tiếp tục trở thành “người hùng thầm lặng” truy tìm công lý đặc biệt càng quyết liệt hơn sau cái chết của người bạn thân Susan.', 'videos/1-201811787.mp4', 'NCC001', 'phim/1-201811787.jpg'),
('PH00094', N'Ác Quỷ Ma Sơ', 'TL005', 90, '2018-12-09', N'Sắp chiếu', N'Lấy bối cảnh một tu viện thuộc Romania năm 1952, trước những sự kiện diễn ra trong "The Conjuring" và "Annabelle". Sau cái chết kỳ dị và bí ẩn của một nữ tu trẻ ở tu viện, một linh mục với quá khứ ám ảnh và một mục sư chưa thực hiện lời tuyên thệ cuối cùng được Vatican cử đến để điều tra sự việc.', 'videos/1-201810775.mp4', 'NCC002', 'phim/1-201810775.jpg'),
('PH00095', N'Maquia: Chờ Ngày Lời Hứa Nở Hoa', 'TL006', 120, '2018-12-11', N'Sắp chiếu', N'Câu chuyện bắt đầu với Maquia, người đến từ một gia đình, nơi tất cả các thành viên đều ngừng tuổi ở giữa những thiếu niên. Cô ấy không có cha mẹ và, mặc dù những ngày của cô ấy là bình thường, cô ấy cảm thấy cô đơn.', 'videos/1-201811288.mp4', 'NCC003', N'phim/1-201811288.jpg'),
('PH00096', N'Những Câu Chuyện Bên Sông', 'TL003', 120, '2019-01-09', N'Sắp chiếu', N'Kể về mối quan hệ kì lạ nhưng bền chặt giữa ba thiếu niên khác biệt nhau mọi mặt. Wakakusa Haruna (Fumi Nikaido đóng), một cô nàng cá tính mạnh, thấy chuyện bất bình chẳng tha. Yamada Ichiro (Ryo Yoshizawa đóng), chàng học sinh gương mẫu và tử tế. Tuy chuyên bị bắt nạt nhưng cậu không hề run sợ trước hoàn cảnh. Ngược lại, cậu tỏ vẻ ngoan cường và ung dung. Một đứa đầy bí ẩn. Yoshikawa Kozue (Sumire đóng), cô bé người mẫu chuyên nghiệp mang dáng người mảnh khảnh và quyến rũ. Cô bé có thói quen ăn rất nhiều để rồi nôn ra hết. Ba người bọn họ gắn kết và san sẻ với nhau một báu vật tang tóc. Đó là một cái xác người chết khô vô danh trên đồng cỏ hoang bên bờ sông chảy ngang thành phố họ sống. Phim là bản nhạc ảm đạm và buồn bã của tuổi thanh xuân đầy những nỗi niềm băn khoăn về thực tại và tương lai, sự sống và cái chết, tình yêu và ích kỷ… Một chiến trường không súng hư.', 'videos/1-201811528.mp4', 'NCC004', N'phim/1-201811528.jpg'),
('PH00097', N'Chân Nhỏ, Bạn Ở Đâu', 'TL002', 100, '2019-01-29', N'Sắp chiếu', N'Sau lần chạm trán với Percy, một sinh vật với đôi chân bé nhỏ, người tuyết Migo quyết tâm thực hiện chuyến phiêu lưu của mình đến vùng đất xa xôi để chứng minh với cộng đồng của mình rằng Chân Nhỏ là có thật.', 'videos/1-201811152.mp4', 'NCC005', N'phim/1-201811152.jpg'),
('PH00098', N'Thiên Hạ Đệ Nhất Tiêu Cục', 'TL001', 100, '2019-01-11', N'Sắp chiếu', N'Ngô Tam Quới qua đời, tướng Mã Bảo vì bảo vệ truyền nhân của chủ tử đành phải nhờ giang hồ giúp sức. Chứng tỏ được năng lực, Thái Bình tiêu cục được Mã Bảo ủy thác hộ tống một người thần bí đến nơi an toàn. Nhóm tiêu sư quả cảm do tiêu đầu Lục An chỉ huy phấn khởi lên đường nhưng không biết mình sẽ đối mặt với mối nguy hiểm khôn lường.', 'videos/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201811271.mp4', 'NCC006', N'phim/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201811271.jpg'),
('PH00099', N'Trò Chơi Giải Mã', 'TL003', 100, '2019-01-18', N'Sắp chiếu', N'Thần đồng IT ẩn dật Lý Hạo Minh bất ngờ bị cuốn vào một phi vụ bất chính. Anh vừa phải cùng hacker Kiều Phi và đả nữ Tô Nghi tấn công tập đoàn IT Oasis theo yêu cầu của Takeshi Mori, tay trùm người Nhật, vừa phải làm tình báo cho cảnh sát.', 'videos/1-201810703.mp4', 'NCC007', N'phim/1-201810703.jpg'),
('PH00100', N'Cá Mập Siêu Bạo Chúa', 'TL005', 120, '2019-01-26', N'Sắp chiếu', N'Bộ phim Cá Mập Siêu Bạo Chúa dựa trên cuốn tiểu thuyết MEG: A Novel of Deep Terror của tác giả Steve Alten, lần đầu xuất bản năm 1997. Câu chuyện bắt đầu khi các nhà khoa học khám phá ra loài cá mập cổ đại Megalodon vẫn còn sống sót tại khu vực biển Mariana Trench nhờ dòng nước lạnh.', 'videos/1-201809481.mp4', 'NCC008', N'phim/1-201809481.jpg'),
('PH00101', N'Johnny English 3: Tái Xuất Giang Hồ', 'TL002', 100, '2019-01-10', N'Sắp chiếu', N'Là phần thứ ba của loạt phim hài Johnny English, với Rowan Atkinson trong vai một gã bỗng dưng trở thành một điệp viên bí mật. Cuộc phiêu lưu mới bắt đầu khi một vụ điều tra hệ thống an ninh mạng cho thấy danh tính của tất cả các điệp viên đang hoạt động tại Anh, và Johnny là hy vọng cuối cùng để điều tra bí mật ấy.', 'videos/1-201804959.mp4', 'NCC009', N'phim/1-201804959.jpg'),
('PH00102', N'Thôn Cổ Sơn', 'TL003', 85, '2019-01-12', N'Sắp chiếu', N'Vào đêm giao thừa âm lịch, Qingyi và con trai ông, Dawa, đợi chồng Shizi đi làm về nước. Thật không may, họ đã chờ một đêm nhưng Shizi chưa bao giờ xuất hiện. Trong khi đó, Dawa tiếp tục nhìn thấy một người xa lạ có vẻ vô hình với Qingyi. Dì Er, hàng xóm của Qingyi, đã đến thăm bà. Qingyi yêu cầu cô chăm sóc Dawa để cô có thể đến ủy ban làng và gọi Shizi tại nơi làm việc.', 'videos/2-201810665.mp4', 'NCC010', N'phim/2-201810665.jpg'),
('PH00103', N'Gã Không Mặt', 'TL005', 100, '2019-01-17', N'Sắp chiếu', N'Được biết đến như một nhân vật kinh dị đầy hãi hùng, Slender Man hiện thân trong hình tượng của người đàn ông mặc vest lịch lãm nhưng đáng sợ với gương mặt trống trơn, thân người cao bất thường.', 'videos/ga-201810986.mp4', 'NCC001', N'phim/ga-201810986.jpg'),
('PH00104', N'Alpha: Người Thủ Lĩnh', 'TL001', 120, '2019-02-15', N'Sắp chiếu', N'Lấy bối cảnh 20.000 năm trước, đưa người xem theo chân một cậu bé thuộc tộc người hang động tại Châu Âu (do Kodi Smit-McPhee thủ vai) vào thời Đồ Đá Cũ. Trong lúc cùng các đồng bào của mình đi săn bò rừng, cậu đã bị rơi khỏi vách đá và lạc mất đoàn. Trong hành trình trở về bộ tộc đầy cam go, người chiến binh trẻ đã có một tình bạn kỳ lạ cùng một con sói tuyết hung dữ.', 'videos/1-201810815.mp4', 'NCC002', N'phim/1-201810815.jpg'),
('PH00105', N'Tìm Kiếm', 'TL003', 120, '2019-02-13', N'Sắp chiếu', N'Sau khi cô con gái 16 tuổi bị mất tích, người cha tuyệt vọng truy cập vào máy tính cá nhân của cô con gái nhằm tìm ra các manh mối để tìm kiếm cô.', 'videos/1-201810228.mp4', 'NCC003', N'phim/1-201810228.jpg'),
('PH00106', N'Bất Khả Xâm Phạm', 'TL001', 90, '2019-02-28', N'Sắp chiếu', N'Dựa trên manga cùng tên của tác giả Arata Miyatsuki và Yuya Kanzaki. Bản điện ảnh đổi giới tính của nhân vật Tomoko Tada trong nguyên tác từ nam sang nữ. Tadashi Usobuki là một sát thủ bí ẩn mặc đồ đen. Usobuki nhận hợp đồng từ những khách hàng chứa chất đầy căm uất. Tuy nhiên việc tìm ra chứng cứ để khép tội Usobuki là vô vọng bởi bởi anh ta có một khả năng đặc biệt. Những nạn nhân sau khi giao tiếp với Usobuki đều mất kiểm soát và bị anh ta chi phối tâm trí.', 'videos/1-201810693.mp4', 'NCC004', N'phim/1-201810693.jpg'),
(N'PH00107', N'Vũ Điệu Tình Yêu', 'TL005', 90, '2019-02-15', N'Sắp chiếu', N'Thời còn trẻ, Lộ Kiến Quốc hy sinh cơ hội trở thành vũ công xuất sắc, đổi lại được tình yêu đẹp với bạn gái Diệp Lan. 20 năm sau, vì tính toán của cuộc sống khiến tình cảm hai vợ chồng rạn nứt.', N'videos/1-201810804.mp4', N'NCC005', N'phim/1-201810804.jpg'),
(N'PH00108', N'Mốc 22', 'TL001', 100, '2019-02-13', N'Sắp chiếu', N'Trong bối cảnh hoạt động tình báo trở nên căng thẳng và chính trị phức tạp với vô số âm mưu tấn công vào nước Mỹ, nhóm đặc nhiệm Ground Branch buộc phải nhận nhiệm vụ vận chuyển một “tài sản” bất đắc dĩ từ Đại sứ quán Mỹ ở một nước Đông Nam Á tới sân bay để về lại Mỹ. Tài sản này là một cảnh sát địa phương Li-Noor. Li-Noor là người nắm giữ thông tin khủng bố, hắn chỉ đồng ý trao đổi khi và chỉ khi hắn được an toàn tại Mỹ.', N'videos/2-201810383.mp4', N'NCC006', N'phim/2-201810383.jpg'),
(N'PH00109', N'Miền Đất Thơ Mộng', 'TL003', 120, '2019-02-12', N'Sắp chiếu', N'Cô gái Gabrielle đến từ ngôi làng nhỏ ở miền nam nước Pháp. Gabrielle có ước mơ về tình yêu được coi là thiếu thực tế và có phần điên rồ. Bố mẹ muốn cô kết hôn với Jose, một công nhân đang làm tại trang trại Tây Ban Nha. Anh ta rất chân thành và yêu mến phụ nữ. Hai ông bà nghĩ đây sẽ là chàng trai phù hợp với con gái của mình. Mặc dù sống cùng anh nhưng Gabrielle thề rằng sẽ không bao giờ đặt tình yêu của mình nơi Jose. Cô không muốn sống cuộc đời nhàm chán bị hạn chế bởi xã hội hậu chiến tranh.', N'videos/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201810422.mp4', N'NCC007', N'phim/64a285864e7b14302ef7ce5dee395601d73a1df5349feffa92pimgpshfullsizedistr-201810422.jpg'),
(N'PH00110', N'Sa Hải Ngoại Truyện: Bệnh Nhân', 'TL005', 100, '2019-02-22', N'Sắp chiếu', N'Vì cái chết kì lạ của thủ hạ nên Hoắc Hữu Tâm đã đến Ngô Sơn Cư hỏi tội và sắp đặt khiến Bạch Xà của Ngô Sơn Cư bị mắc bệnh truyền nhiễm kì lạ là móng tay dài, nhưng không ngờ Bạch Xà lại đánh trả lại khiến cho cả hai đều bị mắc bệnh truyền nhiễm đó.', N'videos/sa-201810494.mp4', N'NCC008', N'phim/sa-201810494.jpg'),
(N'PH00111', N'Bí Mật Phu Nhân Thị Trưởng', 'TL003', 120, '2019-02-05', N'Sắp chiếu', N'Một đoạn clip quay trộm cảnh Trình Kiến và vợ của thị trưởng hôn nhau say đắm đang được phát tán trên mạng khiến anh lâm vào cảnh “sống không bằng chết”.', N'videos/1-201810879.mp4', N'NCC009', N'phim/1-201810879.jpg'),
(N'PH00112', N'Ngôi Nhà Có Chiếc Đồng Hồ Ma Thuật', 'TL001', 90, '2019-03-11', N'Sắp chiếu', N'Cậu bé 10 tuổi Lewis Barnavelt chuyển đến New Zebedee, Michigan sống với ông bác phù thủy Jonathan trong một căn nhà cổ xưa kỳ quái, nơi ẩn giấu một chiếc đồng hồ ma thuật đe dọa đến sự tồn vong của Trái Đất. Với sự giúp đỡ của ông bác Jonathan và pháp sư “hàng xóm” Zimmerman, Lewis nhát gan phải giành tất cả sự dũng cảm của mình để ngăn chặn các thế lực tà ác và phá hủy chiếc đồng hồ mang đến ngày tận thế cho toàn thế giới.', N'videos/1-201810197.mp4', N'NCC010', N'phim/1-201810197.jpg'),
(N'PH00113', N'Mật Ngọt', 'TL005', 100, '2019-03-30', N'Sắp chiếu', N'Nao Kogure là một nữ sinh trung học nhút nhát và dễ thương. Một ngày, Taiga Onise hỏi cô ấy "Cậu có muốn hẹn hò với tôi và hứa rằng chúng ta sẽ kết hôn không?" Nao Kogure không thể từ chối Onise, bởi vì cô sợ cậu ấy. Onise có mái tóc đỏ và đôi mắt đâm xuyên đáng sợ. Sự xuất hiện của cậu ấy tạo ấn tượng rằng cậu ấy là một người xấu, nhưng cậu ấy thực sự quan tâm đến người khác và rất thích nấu ăn. Sau khi tìm hiểu Taiga Onise, Nao Kogure bắt đầu có cảm xúc với cậu ấy và mối quan hệ dần này nở.....', N'videos/1-201810592.mp4', N'NCC001', N'phim/1-201810592.jpg'),
(N'PH00114', N'Màn Đêm Kéo Đến', 'TL003', 45, '2019-03-27', N'Sắp chiếu', N'Sau khi tha mạng cho một cô bé trong một cuộc thảm sát do mình dẫn đầu, gã sát thủ của hội Tam Hoàng Ito (Joe Taslim) trở thành tầm ngắm của hàng loạt sát thủ khác. Phim nhiều cảnh bạo lực, máu me. Cân nhắc trước khi xem.', N'videos/1-201810190.mp4', N'NCC002', N'phim/1-201810190.jpg'),
(N'PH00115', N'Cry - Baby 1990', 'TL001', 100, '2019-03-21', N'Sắp chiếu', N'Đây là bộ phim đầu tiên đánh dấu tên tuổi của Depp tại Hollywood và cũng là một trong những bộ phim hay nhất của anh, nên đã là Depphead thì không thể bỏ qua phim này.', N'videos/c-201810463.mp4', N'NCC003', N'phim/c-201810463.jpg'),
(N'PH00116', N'Biệt Đội Biển Xanh', 'TL005', 100, '2019-03-21', N'Sắp chiếu', N'Vào năm 2100, khi con người bỏ rơi trái đất, một quần thể sinh vật xa hoa vẫn còn phát triển mạnh trong vực thẳm sâu nhất của đại dương. Deep, một con bạch tuộc "phiêu lưu" thích mạo hiểm sống cùng với hai người bạn: Evo, một con cá sấu náo loạn và vụng về, và Alice, một con tôm sâu thần kinh.', N'videos/1280x720-qh4-201706435.mp4', N'NCC004', N'phim/1280x720-qh4-201706435.jpg'),
(N'PH00117', N'Lữ Đoàn Sói', 'TL001', 140, '2019-03-09', N'Sắp chiếu', N'Lấy bối cảnh tương lai năm 2029, Illang: The Wolf Brigade kể về một đội quân đặc nhiệm được lập ra để đàn áp tổ chức khủng bố chống lại sự hình thành tổ chức chính phủ chung giữa Nam Hàn và Bắc Hàn. Phim được chuyển thể từ phim hoạt hình Nhật Bản Jin-Roh: The Wolf Brigade (Lữ Đoàn Sói) của đạo diễn Hiroyuki Okiura.', N'videos/1-201810497.mp4', N'NCC005', N'phim/1-201810497.jpg'),
